function qsort!(xs::Array{Int64,1}, n::Int64)
    i = 1
    begins = Array{Int64}(1000)
    ends = Array{Int64}(1000)
    begins[1] = 1
    ends[1] = n + 1
    while i >= 1
        left = begins[i]
        right = ends[i] - 1
        if left < right
            pivot = xs[left]
            if i == 1000
                return false
            end
            while left < right
                while xs[right] >= pivot && left < right
                    right -= 1
                end
                if left < right
                    xs[left] = xs[right]
                    left += 1
                end
                while xs[left] <= pivot && left < right
                    left += 1
                end
                if left < right
                    xs[right] = xs[left]
                    right -= 1
                end
            end
            xs[left] = pivot
            begins[i + 1] = left + 1
            ends[i + 1] = ends[i]
            ends[i] = left
            i += 1
        else
            i -= 1
        end
    end
    return true
end

